    public static int countFloors(String input) {
        int floor = 0;
        for(char c : input.toCharArray()) {
            if(c == '(') floor++;
            else floor--;
        }
        return floor;
    }
    public static int findBasementCharacter(String input) {
        int floor = 0;
        int count = 0;
        for(char c : input.toCharArray()) {
            count ++;
            if(c == '(') floor++;
            else floor--;
            if (floor<0) break;
        }
        return count;
    }
